package org.directwebremoting.event;import java.util.EventListener;/** * Causes an object to be notified when it is bound to or unbound from a * session. The object is notified by an {@link ScriptSessionBindingEvent} * object. This may be as a result of a servlet programmer explicitly unbinding * an attribute from a session, due to a session being invalidated, or due to a * session timing out. * @see org.directwebremoting.ScriptSession * @see ScriptSessionBindingEvent */public interface ScriptSessionBindingListener extends EventListener{    /**     * Notifies the object that it is being bound to a session and identifies     * the session.     * @param event the event that identifies the session     * @see #valueUnbound(ScriptSessionBindingEvent)     */    public void valueBound(ScriptSessionBindingEvent event);    /**     * Notifies the object that it is being unbound from a session and     * identifies the session.     * @param event the event that identifies the session     * @see #valueBound(ScriptSessionBindingEvent)     */    public void valueUnbound(ScriptSessionBindingEvent event);}